import React from "react";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Table from "react-bootstrap/Table";
import FetchAction from "../redux/actions/fetchAction";

function ResultTable() {
  //1. We call "useDispatch" (React hook) to trigger our request petition
  const dispatch = useDispatch();

  //2. We define a function that combines dispatch function + redux action
  const getFirstFetch = () => dispatch(FetchAction());

  //3. We call useEffect to trigger the request function defined on item 2.
  //The outcome will be saved on the redux store to be accessible from anywhere & anytime
  useEffect(async () => {
    await getFirstFetch();
  }, []);

  const getData = () => {
    getFirstFetch();
  };
  //Selectors
  const oneFetch = useSelector((state) => state.firstFetch.mainInformation);
  const departuresFetch = useSelector(
    (state) => state.fetchDepartures.allDepartures
  );

  let fetchInfo = [];
  if (departuresFetch.departures) {
    let fetchInfo = departuresFetch.departures;
  }

  let tableTest = "";
  if (departuresFetch.departures) {
    tableTest = (
      <tr>
        <td>{departuresFetch.departures[0].departure_time}</td>
        <td>{departuresFetch.departures[0].arrival_time}</td>
        <td>{departuresFetch.departures[0].prices.total}</td>
        <td>{oneFetch.cities[0].short_name}</td>
        <td>{oneFetch.cities[1].short_name}</td>
      </tr>
    );
  }



  async function fetchRepoInfos () {  
    // load repository details for this array of repo URLs
    const repos = [
      {
        url: 'https://api.github.com/repos/fs-opensource/futureflix-starter-kit'
      },
      {
        url: 'https://api.github.com/repos/fs-opensource/android-tutorials-glide'
      }
    ]

    // map through the repo list
    const promises = repos.map(async repo => {
      // request details from GitHub’s API with Axios
      const response = await Axios({
        method: 'GET',
        url: repo.url,
        headers: {
          Accept: 'application/vnd.github.v3+json'
        }
      })

      return {
        name: response.data.full_name,
        description: response.data.description
      }
    })

    // wait until all promises resolve
    const results = await Promise.all(promises)

    // use the results
  }




  return (
    <div>
      {tableTest}
      <div>
        {fetchInfo.map((depart) => {
          <p>{depart.departure_time}</p>;
        })}
      </div>
      <button onClick={getData}>Get data</button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Price (in USD)</th>
            <th>Origin</th>
            <th>Destination</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>3</td>
            <td colSpan="2">Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default ResultTable;
