import React from "react";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Table from "react-bootstrap/Table";
import FetchAction from "../redux/actions/fetchAction";

function ResultTable() {
  //1. We call "useDispatch" (React hook) to trigger our request petition
  const dispatch = useDispatch();

  //2. We define a function that combines dispatch function + redux action
  const getFirstFetch = () => dispatch(FetchAction());

  //3. We call useEffect to trigger the request function defined on item 2.
  //The outcome will be saved on the redux store to be accessible from anywhere & anytime
  useEffect(() => {
    getFirstFetch();
  }, []);

  const getData = () => {
    getFirstFetch();
  };
  //Selectors
  const oneFetch = useSelector((state) => state.firstFetch.mainInformation);
  const departuresFetch = useSelector(
    (state) => state.fetchDepartures.allDepartures
  );

  console.log(departuresFetch);

  let tableTest = "";
  if (departuresFetch) {
    tableTest = (
      <div>
        {departuresFetch.departures[0].arrival_timezone}
        <tr>
          <td>{departuresFetch.departures[0].arrival_timezone}</td>
          <td>Mark</td>
          <td>sadsaf</td>
        </tr>
      </div>
    );
  }

  return (
    <div>
      {tableTest}
      <button onClick={getData}>Get data</button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Mark</td>
            <td>dasdasfsa</td>
          </tr>
          <tr>
            <td>2</td>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <td>3</td>
            <td colSpan="2">Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default ResultTable;
