import React from "react";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Table from "react-bootstrap/Table";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import FetchAction from "../redux/actions/fetchAction";
import Moment from "react-moment";
import "moment-timezone";

function ResultTable() {
  //1. We call "useDispatch" (React hook) to trigger our request petition
  const dispatch = useDispatch();

  //2. We define a function that combines dispatch function + redux action
  const getFirstFetch = () => dispatch(FetchAction());

  //3. We call useEffect to trigger the request function defined on item 2.
  //The outcome will be saved on the redux store to be accessible from anywhere & anytime
  const getData = () => {
    getFirstFetch();
  };
  //Selectors
  const oneFetch = useSelector((state) => state.firstFetch.mainInformation);
  const departuresFetch = useSelector(
    (state) => state.fetchDepartures.allDepartures.departures
  );

  return (
    <div>
      <div>
        <Form>
          <Form.Group>
            <Form.Label>Select City</Form.Label>
            <Form.Control as="select">
              <option>Quebec City, QC, Canada</option>
              <option>Montreal, QC, Canada</option>
            </Form.Control>
            <Form.Row>
              <Col>
                <Form.Label>Select Origin</Form.Label>
                <Form.Control as="select">
                  <option>Quebec City, QC, Canada</option>
                </Form.Control>
              </Col>
              <Col>
                <Form.Label>Select Destination</Form.Label>
                <Form.Control as="select">
                  <option>Quebec City, QC, Canada</option>
                </Form.Control>
              </Col>
            </Form.Row>
          </Form.Group>
        </Form>
      </div>
      <button onClick={getData}>Get data</button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Price</th>
            <th>Origin</th>
            <th>Destination</th>
          </tr>
        </thead>
        <tbody>
          {departuresFetch?.map((depart, i) => (
            <tr key={i}>
              <td>
                <Moment
                  format="MMMM Do YYYY, h:mm:ss a"
                  date={depart.departure_time}
                />
              </td>
              <td>
                <Moment
                  format="MMMM Do YYYY, h:mm:ss a"
                  date={depart.arrival_time}
                />
              </td>
              <td>
                {depart.prices.total} {depart.prices.currency}
              </td>
              <td>{oneFetch.cities[0].short_name}</td>
              <td>{oneFetch.cities[1].short_name}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default ResultTable;
