import React from "react";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Table from "react-bootstrap/Table";
import FetchAction from "../redux/actions/fetchAction";

function ResultTable() {
  //1. We call "useDispatch" (React hook) to trigger our request petition
  const dispatch = useDispatch();

  //2. We define a function that combines dispatch function + redux action
  const getFirstFetch = () => dispatch(FetchAction());

  //3. We call useEffect to trigger the request function defined on item 2.
  //The outcome will be saved on the redux store to be accessible from anywhere & anytime
  useEffect(() => {
    getFirstFetch();
  }, []);

  const getData = () => {
    getFirstFetch();
  };
  //Selectors
  const oneFetch = useSelector((state) => state.firstFetch.mainInformation);
  const departuresFetch = useSelector(
    (state) => state.fetchDepartures.allDepartures
  );

  let fetchInfo = [];
  if (departuresFetch) {
    let fetchInfo = departuresFetch.departures;
  }

  let tableTest = "";
  if (departuresFetch.departures) {
    tableTest = (
      <tr>
        <td>{departuresFetch.departures[0].departure_time}</td>
        <td>{departuresFetch.departures[0].arrival_time}</td>
        <td>{departuresFetch.departures[0].prices.total}</td>
        <td>{oneFetch.cities[0].short_name}</td>
        <td>{oneFetch.cities[1].short_name}</td>
      </tr>
    );
  }

  {
    fetchInfo.map((depart) => {
      return (
        <ul>
          <li>{depart.departure_time}</li>
          <li>{depart.arrival_time}</li>
          <li>{depart.prices.total}</li>
          <li>{oneFetch.cities[0].short_name}</li>
          <li>{oneFetch.cities[1].short_name}</li>
        </ul>
      );
    });
  }

  return (
    <div>
      {tableTest}
      <div>
        {fetchInfo.map((depart) => {
          <ul>
            <li>{depart.departure_time}</li>
            <li>{depart.arrival_time}</li>
            <li>{depart.prices.total}</li>
            <li>{oneFetch.cities[0].short_name}</li>
            <li>{oneFetch.cities[1].short_name}</li>
          </ul>;
        })}
      </div>

      <button onClick={getData}>Get data</button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Price (in USD)</th>
            <th>Origin</th>
            <th>Destination</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            {fetchInfo.map((depart) => {
              <tr>
                <td>{depart.departure_time}</td>
                <td>{depart.arrival_time}</td>
                <td>{depart.prices.total}</td>
                <td>{oneFetch.cities[0].short_name}</td>
                <td>{oneFetch.cities[1].short_name}</td>
              </tr>;
            })}
            <td>3</td>
            <td colSpan="2">Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default ResultTable;
