import React from "react";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Table from "react-bootstrap/Table";
import FetchAction from "../redux/actions/fetchAction";

function ResultTable() {
  //1. We call "useDispatch" (React hook) to trigger our request petition
  const dispatch = useDispatch();

  //2. We define a function that combines dispatch function + redux action
  const getFirstFetch = () => dispatch(FetchAction());

  //3. We call useEffect to trigger the request function defined on item 2.
  //The outcome will be saved on the redux store to be accessible from anywhere & anytime
  //useEffect(async () => {
  //await getFirstFetch();
  //}, []);

  const getData = () => {
    getFirstFetch();
  };
  //Selectors
  const oneFetch = useSelector((state) => state.firstFetch.mainInformation);
  const departuresFetch = useSelector((state) => state.fetchDepartures);

  let fetchInfo = "";
  if (departuresFetch.departures) {
    fetchInfo = (
      <div>
departuresFetch
      </div>
    );
  } else {
    fetchInfo = "nothing";
  }

  return (
    <div>
      {fetchInfo}

      <button onClick={getData}>Get data</button>
    </div>
  );
}

export default ResultTable;
